          </div>
        </div>
        <button className="profile__add-button opacity" onClick={onAddPlace}></button>
      </section>
      <section className="cards">
        {cards.map((card) => {
          return (
            <Card
            card={card}
            link={card.link}
            name={card.name}
            likes={card.likes}
            key={card._id}
            onCardClick={onCardClick}
            onCardLike={onCardLike}
            ></Card>
          )
        })}
      </section>
    </main>
  )
}

export default Main;
