import logo from './logo.svg';
import './App.css';

function App() {
  return (
    <div className="App">
      <div class="popup" id="edit-profle">
    <div class="popup__container">
      <button class="popup__close-button opacity" id="close-edit-popup"></button>
      <form class="form" name="profile" novalidate>
        <h3 class="form__heading">Редактировать профиль</h3>
        <fieldset class="form__input-container">
          <input type="text" name="name" class="form__input" id="name" required minlength="2" maxlength="40" placeholder="Имя"/>
          <span class="form__input-error name-error">#</span>
          <input type="text" name="description" class="form__input" id="title" required minlength="2" maxlength="200" placeholder="О себе"/>
          <span class="form__input-error title-error">#</span>
        </fieldset>
        <fieldset class="form__button-container">
          <button class="form__button">Сохранить</button>
        </fieldset>
      </form>
    </div>
  </div>
    </div>
  );
}

export default App;
