import { useState } from "react";
import { Link } from "react-router-dom";

function Register({onRegister}) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  return (
    <section className="auth">
      <div className="auth__container">
        <h2 className="auth__header">Регистрация</h2>
        <form className="auth__form">
          <input className="auth__input" placeholder="Email" type="email" required></input>
          <input className="auth__input" placeholder="Пароль" type="password" required minLength="5" maxLength="15"></input>
          <button className="auth__button opacity">Зарегистрироваться</button>
        </form>
        <p className="auth__footnote">Уже зарегистрированы? <Link to="/sign-in" className="auth__footnote-hyperlink opacity">Войти</Link></p>
      </div>
    </section>
  )
}

export default Register;
