function handleEditAvatarClick() {
  const avatarPopup = document.querySelector('#upload-avatar');
  avatarPopup.classList.add('popup_opened');
};

function handleEditProfileClick() {
  const editingPopup = document.querySelector('#edit-profle');
  editingPopup.classList.add('popup_opened');
};

function handleAddPlaceClick() {
  const addingPopup = document.querySelector('#add-card');
  addingPopup.classList.add('popup_opened');
};

function Main() {
  return (
    <main className="main">
      <section className="profile">
        <div className="profile__info">
          <button className="profile__edit-avatar" onClick={handleEditAvatarClick}>
            <img src="#" alt="Аватар" className="profile__avatar"/>
          </button>
          <div className="profile__first-line">
            <h1 className="profile__name"></h1>
            <button className="profile__edit-button opacity" onClick={handleEditProfileClick}></button>
            <p className="profile__title"></p>
          </div>
        </div>
        <button className="profile__add-button opacity" onClick={handleAddPlaceClick}></button>
      </section>
      <section className="cards">
      </section>
    </main>
  )
}

export default  Main;
