import React, { useState, useEffect } from "react";
import '../pages/index.css';
import Header from './Header.js';
import Main from './Main.js';
import Footer from './Footer.js';
import PopupWithForm from './PopupWithForm.js';
import ImagePopup from './ImagePopup.js';

function App() {
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  };

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  };

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  };

  return (
    <div className="page">
      <PopupWithForm
        name="edit-profle"
        title="Редактировать профиль"
        isOpen={isEditProfilePopupOpen}
        buttonText="Сохранить"
      >
        <input type="text" name="name" class="form__input" id="name" required minLength="2" maxLength="40" placeholder="Имя"/>
          <span class="form__input-error name-error">#</span>
          <input type="text" name="description" class="form__input" id="title" required minLength="2" maxLength="200" placeholder="О себе"/>
          <span class="form__input-error title-error">#</span>
      </PopupWithForm>
      <ImagePopup></ImagePopup>
      <div className="page__container">
        <Header></Header>
        <Main 
        onEditAvatar={handleEditAvatarClick}
        onEditProfile={handleEditProfileClick}
        onAddPlace={handleAddPlaceClick}/>
        <Footer></Footer>
      </div>
      <template id="create-card-template">
        <div className="card">
          <img src="#" alt="#" className="card__image"/>
          <button className="card__trash opacity"></button>
          <div className="card__elements">
            <h2 className="card__name"></h2>
            <div className="card__like-container">
              <button className="card__like"></button>
              <p className="card__like-counter">#</p>
            </div>
          </div>
        </div>
      </template>
    </div>
  );
}

export default App;
